import axios from 'axios';
import React, { useEffect } from 'react';
import { useState } from 'react/cjs/react.development';
import NewsItems from './NewsItems.js';
import Button from '@mui/material/Button';
import ArrowLeftIcon from '@mui/icons-material/ArrowLeft';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';

export default function Newsapi(props) {
    const [page, setpage]=useState(1);
    const [data, setdata] = useState([]);
    const [totalresults, settotalresults]=useState(0)

    useEffect(() => {
        axios
            .get(
                `https://newsapi.org/v2/top-headlines?country=us&category=${props.category}&apiKey=2af806ae0dbe4a04ab70b10093af5f01&page=${page}&pageSize=20`
            )
            .then((res) => {
                setdata(res.data.articles);
                settotalresults(res.totalresults);
            })
            .catch((err) => console.log(err));
    }, [page]);

    const handleprevclick=()=>{
        setpage(page-1);
    }

    const handlenextclick=()=>{
        setpage(page+1);
    }

    const numpages = (Math.ceil(totalresults/20));

    return (
        <>
            <div className="container d-flex justify-content-between my-3">
                <Button
                    variant="contained"
                    color="secondary"
                    startIcon={<ArrowLeftIcon />}
                    onClick={handleprevclick}
                    disabled={page<=1}
                >
                    previous
                </Button>
                <Button
                    variant="contained"
                    color="secondary"
                    endIcon={<ArrowRightIcon />}
                    onClick={handlenextclick}
                    disabled={page=numpages}
                >
                    next
                </Button>
            </div>
            <div className="my-4">
                <NewsItems data={data} />
            </div>
        </>
    );
}
