import axios from 'axios';
import React, { useEffect } from 'react';
import { useState } from 'react/cjs/react.development';
import NewsItems from './NewsItems.js';
import Button from '@mui/material/Button';
import ArrowLeftIcon from '@mui/icons-material/ArrowLeft';

export default function Newsapi(props) {
    const [data, setdata] = useState([]);

    useEffect(() => {
        axios
            .get(
                `https://newsapi.org/v2/top-headlines?country=us&category=${props.category}&apiKey=2af806ae0dbe4a04ab70b10093af5f01&page=2`
            )
            .then((res) => {
                setdata(res.data.articles);
            })
            .catch((err) => console.log(err));
    }, []);

    return (
        <>
            <div className="my-4">
                <NewsItems data={data} />
            </div>
            <div className="container d-flex justify-content-between my-5">
                <Button variant="contained" color="secondary" startIcon={<ArrowLeftIcon/>}>
                    previous
                </Button>
                <Button variant="contained" color="secondary">
                    next
                </Button>
            </div>
        </>
    );
}
