import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    text: {
        color: 'black',
        fontWeight: 'bold',
    },
    grid: {
        paddingTop:'22px',
        width: '250px',
        boxShadow:'0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)',
    },
});

export { useStyles };
