import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core';
import clsx from 'clsx';

const useStyles = makeStyles((theme) => ({
    btn: {
        textDecoration: 'none',
        color: 'white',
        borderRadius: 5,
        [theme.breakpoints.down('sm')]: {
            display: 'none',
        },
    },
    btndark: {
        backgroundColor: '#222831',
        marginLeft: '10px',
        marginRight: '10px',
    },
    btnsuc: {
        backgroundColor: '#1DCD9F',
        marginLeft: '10px',
        marginRight: '10px',
    },
    btninfo: {
        backgroundColor: '#f50057',
    },
    drop: {
        [theme.breakpoints.up('md')]: {
            display: 'none',
        },
    },
    text:{
        textDecoration:'none'
    }
}));
export default function Navbar() {
    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
        setAge(event.target.value);
    };

    const classes = useStyles();
    return (
        <>
            <Box sx={{ flexGrow: 1 }}>
                <AppBar position="static" color="primary">
                    <Toolbar>
                        <Typography
                            variant="h6"
                            component="div"
                            sx={{ flexGrow: 1 }}
                        >
                            News
                        </Typography>
                        <Link
                            to="/Business"
                            className={clsx(classes.btn, classes.btninfo)}
                        >
                            <Button color="inherit">business</Button>
                        </Link>
                        <Link
                            to="/Entertainment"
                            className={clsx(classes.btn, classes.btndark)}
                        >
                            <Button color="inherit">entertainment</Button>
                        </Link>
                        <Link
                            to="/General"
                            className={clsx(classes.btn, classes.btninfo)}
                        >
                            <Button color="inherit">general</Button>
                        </Link>
                        <Link
                            to="/Health"
                            className={clsx(classes.btn, classes.btnsuc)}
                        >
                            <Button color="inherit">health</Button>
                        </Link>
                        <Link
                            to="/Science"
                            className={clsx(classes.btn, classes.btninfo)}
                        >
                            <Button color="inherit">science</Button>
                        </Link>
                        <Link
                            to="/Sports"
                            className={clsx(classes.btn, classes.btndark)}
                        >
                            <Button color="inherit">sports</Button>
                        </Link>
                        <Link
                            to="/Technology"
                            className={clsx(classes.btn, classes.btninfo)}
                        >
                            <Button color="inherit">technology</Button>
                        </Link>
                        <Box sx={{ minWidth: 120 }} className={classes.drop}>
                            <FormControl fullWidth className="bg-light">
                                <InputLabel id="demo-simple-select-label" className="my-3">
                                    Category
                                </InputLabel>
                                <Select
                                    labelId="demo-simple-select-label"
                                    id="demo-simple-select"
                                    label="category"
                                    value={age}
                                    onChange={handleChange}
                                    color="secondary"
                                >
                                    <MenuItem value="BUSINESS" >
                                        <Link to="/Business" className={classes.text}>
                                            BUSINESS
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="ENTERTAINMENT">
                                        <Link to="/Entertainment" className={classes.text}>
                                            ENTERTAINMENT
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="GENERAL">
                                        <Link to="/General" className={classes.text}>
                                            GENERAL
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="HEALTH">
                                        <Link to="/Health" className={classes.text}>
                                            HEALTH
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="SCIENCE">
                                        <Link to="/Science" className={classes.text}>
                                            SCIENCE
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="SPORTS">
                                        <Link to="/Sports" className={classes.text}>
                                            SPORTS
                                        </Link>
                                    </MenuItem>
                                    <MenuItem value="TECHNOLOGY">
                                        <Link to="/Technology" className={classes.text}>
                                           TECHNOLOGY
                                        </Link>
                                    </MenuItem>
                                </Select>
                            </FormControl>
                        </Box>
                    </Toolbar>
                </AppBar>
            </Box>
        </>
    );
}
