import axios from 'axios'
import React, { useEffect } from 'react'
import { useState } from 'react/cjs/react.development';
import NewsItems from '/home/cryptoknight/Desktop/my-app/src/components/NewsItems.js'

export default function Newsapi(props) {

    const [data, setdata]=useState([]);

    useEffect(()=>{
        axios
        .get(`https://newsapi.org/v2/top-headlines?country=us&category=${props.category}&apiKey=2af806ae0dbe4a04ab70b10093af5f01`)
        .then((res)=>setdata(res.data.articles))
        .catch((err)=>console.log(err));
    },[])

  return (
    <div className="my-4">
      <NewsItems data={data}/>
    </div>
  )
}
