import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import '/home/cryptoknight/Desktop/my-app/src/components/NewsItem.css';
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme)=>({
    text:{

        color : 'black',
        fontWeight: 'bold'
    }
}));

const Item = styled(Paper)(({ theme }) => ({
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));

export default function NewsItems(props) {
    const classes=useStyles();

    let alternate = "https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Ftse1.mm.bing.net%2Fth%3Fid%3DOIP.GnqZiwU7k5f_kRYkw8FNNwHaF3%26pid%3DApi&f=1";
    return (
        <Box sx={{ flexGrow: 1 }}>
            <Grid container spacing={5}>
                {props.data.map((items, index) => {
                    return (
                        <Grid item xs={4} key={index}> 
                            <Item className={classes.text}>{items.title}</Item>
                            <Item>
                                <img
                                    src={items.urlToImage?items.urlToImage:alternate}
                                    alt=""
                                    className="img"
                                />
                            </Item>
                            <Item >
                                {items.content}...
                                <a href={items.url} target="_blank" rel="noreferrer">more</a>
                            </Item>
                        </Grid>
                    );
                })}
            </Grid>
        </Box>
    );
}
